INSERT INTO `wp003_posts` VALUES ('1', '1', '2013-09-22 05:53:15', '2013-09-22 05:53:15', '欢迎使用WordPress。这是系统自动生成的演示文章。编辑或者删除它，然后开始您的博客！', '世界，你好！', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2013-09-22 05:53:15', '2013-09-22 05:53:15', '', '0', 'http://wx.ilongxin.com/mb/003/?p=1', '0', 'post', '', '1');
INSERT INTO `wp003_posts` VALUES ('2', '1', '2013-09-22 05:53:15', '2013-09-22 05:53:15', '这是示范页面。页面和博客文章不同，它的位置是固定的，通常会在站点导航栏显示。很多用户都创建一个“关于”页面，向访客介绍自己。例如，个人博客通常有类似这样的介绍：\n\n<blockquote>欢迎！我白天是个邮递员，晚上就是个有抱负的演员。这是我的博客。我住在天朝的帝都，有条叫做杰克的狗。</blockquote>\n\n……公司博客可以这样写：\n\n<blockquote>XYZ Doohickey公司成立于1971年，自从建立以来，我们一直向社会贡献着优秀doohicky。我们的公司总部位于天朝魔都，有着超过两千名员工，对魔都政府税收有着巨大贡献。</blockquote>\n\n您可以访问<a href=\"http://wx.ilongxin.com/mb/003/wp-admin/\">仪表盘</a>，删除本页面，然后添加您自己的内容。祝您使用愉快！', '示例页面', '', 'publish', 'open', 'open', '', 'sample-page', '', '', '2013-09-22 05:53:15', '2013-09-22 05:53:15', '', '0', 'http://wx.ilongxin.com/mb/003/?page_id=2', '0', 'page', '', '0');
INSERT INTO `wp003_posts` VALUES ('677', '1', '2013-09-25 08:04:22', '2013-09-25 00:04:22', ' ', '', '', 'publish', 'open', 'open', '', '677', '', '', '2013-09-25 08:04:22', '2013-09-25 00:04:22', '', '0', 'http://wx.ilongxin.com/mb/003/?p=677', '1', 'nav_menu_item', '', '0');
INSERT INTO `wp003_posts` VALUES ('706', '1', '2013-10-08 07:49:23', '2013-10-07 23:49:23', '[getLayerSlider title=\"Home slider\"][/getLayerSlider]\n\n[contentBox]\n<h1 class=\"center\">雅尊家具</h1>\n石家庄雅尊品牌家居连锁，成立于2004年，现旗下拥有四大品牌：华典臻木、全实木家具、中国概念（花梨木）新中式家具、优美家。\n\n[/contentBox]\n\n\n[columnSplit type=\"flexible-column\"]\n\n[column align=\"center\"]\n<p style=\"text-align: center;\"><img class=\"aligncenter\" title=\"responsive\" alt=\"\" src=\"http://wx.ilongxin.com/mb/003/wp-content/uploads/2012/10/js.png\" width=\"124\" height=\"106\" />\n<strong>企业介绍</strong></p>\n[/column]\n<div>\n\n[column align=\"center\"]\n<p style=\"text-align: center;\"><img class=\"aligncenter\" title=\"retina-clarity\" alt=\"\" src=\"http://wx.ilongxin.com/mb/003/wp-content/uploads/2012/10/zs.png\" width=\"124\" height=\"106\" />\n<strong>家居展示</strong></p>\n[/column]\n\n</div>\n[column align=\"center\"]\n<p style=\"text-align: center;\"><img class=\"wp-image-295 aligncenter\" title=\"customizable\" alt=\"\" src=\"http://wx.ilongxin.com/mb/003/wp-content/uploads/2012/10/d.png\" width=\"124\" height=\"106\" />\n<strong>最新活动</strong></p>\n[/column]\n\n[column align=\"center\"]\n<p style=\"text-align: center;\"><img class=\"wp-image-296 aligncenter\" title=\"in-motion\" alt=\"\" src=\"http://wx.ilongxin.com/mb/003/wp-content/uploads/2012/10/in-motion.png\" width=\"124\" height=\"106\" /></p>\n<p style=\"text-align: center;\"><strong>店铺指引</strong></p>\n[/column]\n\n[column align=\"center\"]\n<p style=\"text-align: center;\"><img class=\"aligncenter\" title=\"in-motion\" alt=\"\" src=\"http://wx.ilongxin.com/mb/003/wp-content/uploads/2012/10/in-motion.png\" width=\"124\" height=\"106\" /></p>\n<p style=\"text-align: center;\"><strong>生活消费</strong></p>\n[/column]\n\n[column align=\"center\"]\n<p style=\"text-align: center;\"><img class=\"aligncenter\" title=\"in-motion\" alt=\"\" src=\"http://wx.ilongxin.com/mb/003/wp-content/uploads/2012/10/in-motion.png\" width=\"124\" height=\"106\" /></p>\n<p style=\"text-align: center;\"><strong><a href=\"tel:4000000000\">联系我们</a></strong></p>\n[/column]\n\n[/columnSplit]\n\n&nbsp;', 'Home', '', 'inherit', 'open', 'open', '', '234-autosave-v1', '', '', '2013-10-08 07:49:23', '2013-10-07 23:49:23', '', '234', 'http://wx.ilongxin.com/mb/003/?p=706', '0', 'revision', '', '0');
