INSERT INTO `wp003_posts` VALUES ('532', '1', '2012-10-28 11:28:36', '2012-10-28 11:28:36', '[getLayerSlider title=\"Shortcodes\"][/getLayerSlider]\n<div class=\"white-content-box\">\n\n[h2]Forms[/h2]\n<h3>Checkboxes</h3>\n<div data-role=\"fieldcontain\">\n<fieldset data-role=\"controlgroup\"><legend>Choose as many snacks as you\'d like:</legend><input id=\"checkbox-1a\" class=\"custom\" type=\"checkbox\" name=\"checkbox-1a\" /><label for=\"checkbox-1a\">Cheetos</label><input id=\"checkbox-2a\" class=\"custom\" type=\"checkbox\" name=\"checkbox-2a\" /><label for=\"checkbox-2a\">Doritos</label><input id=\"checkbox-3a\" class=\"custom\" type=\"checkbox\" name=\"checkbox-3a\" /><label for=\"checkbox-3a\">Fritos</label><input id=\"checkbox-4a\" class=\"custom\" type=\"checkbox\" name=\"checkbox-4a\" /><label for=\"checkbox-4a\">Sun Chips</label></fieldset>\n</div>\n<div class=\"clear\"></div>\n<h3>Select</h3>\n<select id=\"select-choice-min\" name=\"select-choice-min\" data-mini=\"true\"> <option value=\"standard\">Standard: 7 day</option> <option value=\"rush\">Rush: 3 days</option> <option value=\"express\">Express: next day</option> <option value=\"overnight\">Overnight</option> </select>\n<h3>Flip Switch</h3>\n<div data-role=\"fieldcontain\"><select id=\"flip-mini\" name=\"flip-mini\" data-role=\"slider\" data-mini=\"true\"> <option value=\"off\">Off</option> <option value=\"on\">On</option> </select></div>\n&nbsp;\n\n&nbsp;\n<h2>Tables</h2>\n<strong>.table1</strong>\n<table class=\"table1\" width=\"99%\" cellspacing=\"0\" cellpadding=\"0\">\n<thead>\n<tr>\n<th>#</th>\n<th>Product</th>\n<th>Price</th>\n<th>Status</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td>1</td>\n<td>iPhone 4G</td>\n<td>$599</td>\n<td>Delivered</td>\n</tr>\n<tr>\n<td>2</td>\n<td>HTSee Neo</td>\n<td>$399</td>\n<td>Pending</td>\n</tr>\n<tr>\n<td>2</td>\n<td>Blueberry</td>\n<td>$299</td>\n<td>Failed</td>\n</tr>\n</tbody>\n</table>\n&nbsp;\n\n<strong>.table2</strong>\n<table class=\"table2\" width=\"99%\