INSERT INTO `wp003_posts` VALUES ('699', '1', '2013-09-25 09:25:56', '2013-09-25 01:25:56', '[getLayerSlider title=\"Home slider\"][/getLayerSlider]\r\n\r\n[contentBox]\r\n<h1 class=\"center\">欢迎访问雅尊家具微网页</h1>\r\n石家庄雅尊品牌家居连锁，成立于2004年，现旗下拥有四大品牌：华典臻木、全实木家具、中国概念（花梨木）新中式家具、优美家。\r\n\r\n雅尊秉承真诚服务到永远的态度，从客户第一次光顾店面，贴心管家的服务从此开始，专业设计人员从平面设计到3D效果图，让客户从想象空间转入非常直观地视觉体验；从家装风格的定位到软装饰的搭配，雅尊会提供一条龙服务。\r\n\r\n[/contentBox]\r\n\r\n&nbsp;\r\n\r\n[columnSplit type=\"flexible-column\"]\r\n\r\n[column align=\"center\"]\r\n<p style=\"text-align: center;\"><img class=\"aligncenter\" title=\"responsive\" alt=\"\" src=\"http://wx.ilongxin.com/mb/003/wp-content/uploads/2012/10/responsive.png\" width=\"62\" height=\"53\" />\r\n<strong>企业介绍</strong></p>\r\n[/column]\r\n<div>\r\n\r\n[column align=\"center\"]\r\n\r\n<img title=\"retina-clarity\" alt=\"\" src=\"http://wx.ilongxin.com/mb/003/wp-content/uploads/2012/10/retina-clarity.png\" width=\"48\" />\r\n<strong>家居展示</strong>\r\n\r\n[/column]\r\n\r\n</div>\r\n[column align=\"center\"]\r\n\r\n<img class=\"alignnone  wp-image-295\" title=\"customizable\" alt=\"\" src=\"http://wx.ilongxin.com/mb/003/wp-content/uploads/2012/10/customizable.png\" width=\"48\" />\r\n<strong>最新活动</strong>\r\n\r\n[/column]\r\n\r\n[column align=\"center\"]\r\n\r\n<img class=\"alignnone  wp-image-296\" title=\"in-motion\" alt=\"\" src=\"http://wx.ilongxin.com/mb/003/wp-content/uploads/2012/10/in-motion.png\" width=\"48\" />\r\n\r\n<strong>店铺指引</strong>\r\n\r\n[/column]\r\n\r\n[column align=\"center\"]\r\n\r\n<img title=\"in-motion\" alt=\"\" src=\"http://wx.ilongxin.com/mb/003/wp-content/uploads/2012/10/in-motion.png\" width=\"48\" />\r\n\r\n<strong>生活消费</strong>\r\n\r\n[/column]\r\n\r\n[column align=\"center\"]\r\n\r\n<img title=\"in-motion\" alt=\"\" src=\"http://wx.ilongxin.com/mb/003/wp-content/uploads/2012/10/in-motion.png\" width=\"48\" />\r\n\r\n<strong><a href=\"tel:4000000000\">联系我们</a></strong>\r\n\r\n[/column]\r\n\r\n[/columnSplit]\r\n\r\n&nbsp;', 'Home', '', 'inh