INSERT INTO `wp005_options` VALUES ('139', '_transient_dash_de3249c4736ad3bd2cd29147c4a0d43e', '<h4>最热门的</h4>\n<h5><a href=\'http://wordpress.org/plugins/contact-form-plugin/\'>Contact Form</a></h5>&nbsp;<span>(<a href=\'plugin-install.php?tab=plugin-information&amp;plugin=contact-form-plugin&amp;_wpnonce=13fdefdf8e&amp;TB_iframe=true&amp;width=600&amp;height=800\' class=\'thickbox\' title=\'Contact Form\'>安装</a>)</span>\n<p>Add Contact Form to your WordPress website.</p>\n<h4>最新插件</h4>\n<h5><a href=\'http://wordpress.org/plugins/wp-comment-notes/\'>WP Comment Notes</a></h5>&nbsp;<span>(<a href=\'plugin-install.php?tab=plugin-information&amp;plugin=wp-comment-notes&amp;_wpnonce=f6a33d6d97&amp;TB_iframe=true&amp;width=600&amp;height=800\' class=\'thickbox\' title=\'WP Comment Notes\'>安装</a>)</span>\n<p>Add custom notes before or after the comment form.</p>\n', 'no');
